import React,{useState} from 'react';
import './App.scss';
import './components/Sidebar';
import Sidebar from './components/Sidebar';
import Navbar from './components/Navbar';
import {BrowserRouter,Route} from 'react-router-dom'
import ResetScroll from './components/ResetScroll';
import Home from './pages/Home';
import Article from './pages/Article';
import All from './pages/All';
import NewPost from './pages/NewPost';
import MyArchive from './pages/MyArchive';
import Collection from './pages/Collection';
import MyPosts from './pages/MyPosts';


function App() {

  const data = fetch('http://localhost:5000/api/collection').then(res=>res.json());

  const [tab, setTab] = useState("featured");
  const getTabName = (tabName) => {
    setTab(tabName)    
  }



  return (
    <div className="App">

      <BrowserRouter>
        <Sidebar getTabName={getTabName}/>
        <Navbar />
        <ResetScroll />

        <Route exact path="/" render={()=>(<Home data={tab} />)}/> 
        <Route path="/article" component={Article}/> 
        <Route path="/all" component={All}/>
        <Route path="/new-post" component={NewPost}/>
        <Route path="/archive" component={MyArchive}/>
        <Route path="/collection" component={Collection}/>
        <Route path="/my-posts" component={MyPosts}/>


      </BrowserRouter>


    </div>
  );
}

export default App;
